inputs.where:
@or(
    and(
        not(empty(item()?['E_x002d_Mail'])),
        equals(
            toLower(item()?['E_x002d_Mail']),
            toLower(outputs('Get_response_details')?['body/r8a2b0c1833f24066bc999b42e738f97b'])
        )
    ),
    and(
        not(empty(item()?['Naam'])),
        equals(
            toLower(item()?['Naam']),
            toLower(
                concat(
                   outputs('Get_response_details')?['body/r5ad58f4a0a044fad8179a5c43c478e1c'],
                    ' ',
                   outputs('Get_response_details')?['body/r3c1ce4e7f098408e9ec7c49882f05c0c']
                )
            )
        )
    ),
    and(
        not(empty(item()?['MedewerkerKuijpers']?['EMail'])),
        equals(
            toLower(item()?['MedewerkerKuijpers']?['EMail']),
            toLower(outputs('Get_response_details')?['body/r8a2b0c1833f24066bc999b42e738f97b'])
        )
    )
)
